import React, { useState } from 'react';

function Post({ post, deletePost }) {
  const [isEditPopupOpen, setEditPopupOpen] = useState(false);

  const openEditPopup = () => {
    setEditPopupOpen(true);
  };

  const closeEditPopup = () => {
    setEditPopupOpen(false);
  };

  const delPost = () => {
    deletePost(post.id);
  };

  return (
    <>
      <li key={post.id}>
        <h3>{post.title}</h3>
        <p>{post.body}</p>
        <button onClick={openEditPopup}>Edit</button>
        <button onClick={delPost}>Delete</button>
      </li>

      {/* Edit Popup */}
      {isEditPopupOpen && (
        <div className="edit-popup">
          <h2>Edit Post</h2>
          <label>Title:</label>
          <input type="text" value={post.title} onChange={(e) => console.log(e.target.value)} />
          <label>Body:</label>
          <textarea value={post.body} onChange={(e) => console.log(e.target.value)} />
          <button onClick={closeEditPopup}>Close</button>
          <button>Save</button>
        </div>
      )}
    </>
  );
}

export default Post;
