import React, { useState } from 'react';

function Post({ post, deletePost, editPost }) {
  const [isEditPopupOpen, setEditPopupOpen] = useState(false);
  const [editedTitle, setEditedTitle] = useState(post.title);
  const [editedBody, setEditedBody] = useState(post.body);

  const openEditPopup = () => {
    setEditPopupOpen(true);
  };

  const closeEditPopup = () => {
    setEditPopupOpen(false);
  };

  const delPost = () => {
    deletePost(post.id);
  };

  const saveEditedPost = () => {
  
    editPost(post.id, editedTitle, editedBody);
    closeEditPopup();
  };

  return (
    <>
      <li key={post.id}>
        <h3>{post.title}</h3>
        <p>{post.body}</p>
        <button onClick={openEditPopup}>Edit</button>
        <button onClick={delPost}>Delete</button>
      </li>

      {/* Edit Popup */}
      {isEditPopupOpen && (
        <div className="popup-overlay">
          <div className="edit-popup">
            <h2>Edit Post</h2>
            <label>Title:</label>
            <input
              type="text"
              value={editedTitle}
              onChange={(e) => setEditedTitle(e.target.value)}
            />
            <label>Body:</label>
            <textarea
              value={editedBody}
              onChange={(e) => setEditedBody(e.target.value)}
            />
            <button onClick={closeEditPopup}>Close</button>
            <button onClick={saveEditedPost}>Save</button>
          </div>
        </div>
      )}
    </>
  );
}

export default Post;
