import React, { useState } from 'react';
import Comments from './Comments'; 

function Post({ post, deletePost, editPost, viewComments }) {
  const [isEditPopupOpen, setEditPopupOpen] = useState(false);
  const [editedTitle, setEditedTitle] = useState(post.title);
  const [editedBody, setEditedBody] = useState(post.body);
  const [isCommentsOpen, setCommentsOpen] = useState(false); // New state for comments

  const openEditPopup = () => {
    setEditPopupOpen(true);
  };

  const closeEditPopup = () => {
    setEditPopupOpen(false);
  };

  const delPost = () => {
    deletePost(post.id);
  };

  const saveEditedPost = () => {
    editPost(post.id, editedTitle, editedBody);
    closeEditPopup();
  };

  const toggleComments = () => {
    setCommentsOpen(!isCommentsOpen);
  };

  return (
    <div className="post-card">
      <h3>{post.title}</h3>
      <p>{post.body}</p>
      <div className="post-actions">
        <button onClick={openEditPopup}>Edit</button>
        <button onClick={toggleComments}>View comments</button>
        <button onClick={delPost}>
          <span role="img" aria-label="delete-icon" className="delete-icon">
            üóëÔ∏è
          </span>
        </button>
      </div>

      {isEditPopupOpen && (
        <div className="popup-overlay">
          <div className="edit-popup">
            <h2>Edit Post</h2>
            <label>Title:</label>
            <input
              type="text"
              value={editedTitle}
              onChange={(e) => setEditedTitle(e.target.value)}
            />
            <label>Body:</label>
            <textarea
              value={editedBody}
              onChange={(e) => setEditedBody(e.target.value)}
            />
            <button onClick={closeEditPopup}>Close</button>
            <button onClick={saveEditedPost}>Save</button>
          </div>
        </div>
      )}

      {isCommentsOpen && <Comments postId={post.id} />}
    </div>
  );
}

export default Post;
