import React, { useState } from 'react';
import Post from './Post';

function PostList({ posts, deletePost, editPost }) {
  const [commentsOpenState, setCommentsOpenState] = useState({});

  const toggleComments = (postId) => {
    setCommentsOpenState((prevCommentsOpenState) => ({
      ...prevCommentsOpenState,
      [postId]: !prevCommentsOpenState[postId],
    }));
  };

  return (
    <div>
      {posts.map((post) => (
        <Post
          key={post.id}
          post={post}
          deletePost={deletePost}
          editPost={editPost}
          toggleComments={() => toggleComments(post.id)}
          isCommentsOpen={commentsOpenState[post.id]}
        />
      ))}
    </div>
  );
}

export default PostList;
